mapper.select:
select
,
, e.enum_value, e.enum_value_name from bid_evaluation_report a join tenant_${parentTenantAccount}.enum_var e on a.status = e.enum_value and enum_name = 'TenderResultStatus' where bid_result_code = #{bidResultCode,jdbcType=VARCHAR}

mapper.update:
    update sub_div_work_bill a 
	join supplier_bid_item_rate b on a.division_sn_code = b.division_sn_code
	    set tender_result_code = #{tenderResultCode,jdbcType=VARCHAR}
			, a.supplier_credit_code = #{supplierCreditCode,jdbcType=VARCHAR}
			, a.contract_unit_price = b.first_bid_unit_rate
	where  b.supplier_credit_code = #{supplierCreditCode,jdbcType=VARCHAR}
	and exists (
		select 1 from bid_evaluation_report c
		where c.bid_result_code = #{tenderResultCode,jdbcType=VARCHAR}
		and a.tender_plan_code = c.tender_plan_code
	)
