mapper.select:

select 

from sub_div_work_tender_result_change_bill
where division_sn_code = #{divisionSnCode,jdbcType=VARCHAR}
  and change_code = #{changeCode,jdbcType=VARCHAR}

select 

from sub_div_work_tender_result_change_bill a
where a.change_type = 3
and division_sn_code = #{divisionSnCode,jdbcType=VARCHAR}
and exists (
	select 1
	from tender_result_change b
	where a.change_code = b.tender_result_id_change_code
	and b.status != #{status,jdbcType=INTEGER}
)

mapper.delete:

delete from sub_div_work_tender_result_change_bill
where division_sn_code = #{divisionSnCode,jdbcType=VARCHAR}
  and change_code = #{changeCode,jdbcType=VARCHAR}
